package proyecto_algoritmos;

public class Producto {
	public Producto(int id, String nombre) {
		// TODO Auto-generated constructor stub
		this.id = id;
		this.nombre = nombre;
	}
	
	private int id;
	private String nombre;
	
	public int getId() {
	 	return id;
	}
	
	public String getNombre() {
	 	return nombre;
	}
}
